00635                     ((XQWORDSIG) ((XDWORDSIG) in[0])) * ((XDWORDSIG) in[8]) +
00636                2 * (((XQWORDSIG) ((XDWORDSIG) in[1])) * ((XDWORDSIG) in[7]) +
00637                     ((XQWORDSIG) ((XDWORDSIG) in[3])) * ((XDWORDSIG) in[5])));
00638   output[9] =  2 * (((XQWORDSIG) ((XDWORDSIG) in[4])) * ((XDWORDSIG) in[5]) +
00639                     ((XQWORDSIG) ((XDWORDSIG) in[3])) * ((XDWORDSIG) in[6]) +
00640                     ((XQWORDSIG) ((XDWORDSIG) in[2])) * ((XDWORDSIG) in[7]) +
00641                     ((XQWORDSIG) ((XDWORDSIG) in[1])) * ((XDWORDSIG) in[8]) +
00642                     ((XQWORDSIG) ((XDWORDSIG) in[0])) * ((XDWORDSIG) in[9]));
00643   output[10] = 2 * (((XQWORDSIG) ((XDWORDSIG) in[5])) * ((XDWORDSIG) in[5]) +
00644                     ((XQWORDSIG) ((XDWORDSIG) in[4])) * ((XDWORDSIG) in[6]) +
00645                     ((XQWORDSIG) ((XDWORDSIG) in[2])) * ((XDWORDSIG) in[8]) +
00646                2 * (((XQWORDSIG) ((XDWORDSIG) in[3])) * ((XDWORDSIG) in[7]) +
00647                     ((XQWORDSIG) ((XDWORDSIG) in[1])) * ((XDWORDSIG) in[9])));
00648   output[11] = 2 * (((XQWORDSIG) ((XDWORDSIG) in[5])) * ((XDWORDSIG) in[6]) +
00649                     ((XQWORDSIG) ((XDWORDSIG) in[4])) * ((XDWORDSIG) in[7]) +
00650                     ((XQWORDSIG) ((XDWORDSIG) in[3])) * ((XDWORDSIG) in[8]) +
00651                     ((XQWORDSIG) ((XDWORDSIG) in[2])) * ((XDWORDSIG) in[9]));
00652   output[12] =      ((XQWORDSIG) ((XDWORDSIG) in[6])) * ((XDWORDSIG) in[6]) +
00653                2 * (((XQWORDSIG) ((XDWORDSIG) in[4])) * ((XDWORDSIG) in[8]) +
00654                2 * (((XQWORDSIG) ((XDWORDSIG) in[5])) * ((XDWORDSIG) in[7]) +
00655                     ((XQWORDSIG) ((XDWORDSIG) in[3])) * ((XDWORDSIG) in[9])));
00656   output[13] = 2 * (((XQWORDSIG) ((XDWORDSIG) in[6])) * ((XDWORDSIG) in[7]) +
00657                     ((XQWORDSIG) ((XDWORDSIG) in[5])) * ((XDWORDSIG) in[8]) +
00658                     ((XQWORDSIG) ((XDWORDSIG) in[4])) * ((XDWORDSIG) in[9]));
00659   output[14] = 2 * (((XQWORDSIG) ((XDWORDSIG) in[7])) * ((XDWORDSIG) in[7]) +
00660                     ((XQWORDSIG) ((XDWORDSIG) in[6])) * ((XDWORDSIG) in[8]) +
00661                2 *  ((XQWORDSIG) ((XDWORDSIG) in[5])) * ((XDWORDSIG) in[9]));
00662   output[15] = 2 * (((XQWORDSIG) ((XDWORDSIG) in[7])) * ((XDWORDSIG) in[8]) +
00663                     ((XQWORDSIG) ((XDWORDSIG) in[6])) * ((XDWORDSIG) in[9]));
00664   output[16] =      ((XQWORDSIG) ((XDWORDSIG) in[8])) * ((XDWORDSIG) in[8]) +
00665                4 *  ((XQWORDSIG) ((XDWORDSIG) in[7])) * ((XDWORDSIG) in[9]);
00666   output[17] = 2 *  ((XQWORDSIG) ((XDWORDSIG) in[8])) * ((XDWORDSIG) in[9]);
00667   output[18] = 2 *  ((XQWORDSIG) ((XDWORDSIG) in[9])) * ((XDWORDSIG) in[9]);
00668 }
