00707 void CIPHERCURVE25519::fexpand(XQWORDSIG* output, const XBYTE* input) 
00708 {
00709   output[0] = ((((XQWORDSIG) input[ 0 + 0]) | ((XQWORDSIG) input[ 0 + 1]) << 8 | ((XQWORDSIG) input[ 0 + 2]) << 16 | ((XQWORDSIG) input[ 0 + 3]) << 24) >> 0) & 0x3ffffff;;
00710   output[1] = ((((XQWORDSIG) input[ 3 + 0]) | ((XQWORDSIG) input[ 3 + 1]) << 8 | ((XQWORDSIG) input[ 3 + 2]) << 16 | ((XQWORDSIG) input[ 3 + 3]) << 24) >> 2) & 0x1ffffff;;
00711   output[2] = ((((XQWORDSIG) input[ 6 + 0]) | ((XQWORDSIG) input[ 6 + 1]) << 8 | ((XQWORDSIG) input[ 6 + 2]) << 16 | ((XQWORDSIG) input[ 6 + 3]) << 24) >> 3) & 0x3ffffff;;
00712   output[3] = ((((XQWORDSIG) input[ 9 + 0]) | ((XQWORDSIG) input[ 9 + 1]) << 8 | ((XQWORDSIG) input[ 9 + 2]) << 16 | ((XQWORDSIG) input[ 9 + 3]) << 24) >> 5) & 0x1ffffff;;
00713   output[4] = ((((XQWORDSIG) input[12 + 0]) | ((XQWORDSIG) input[12 + 1]) << 8 | ((XQWORDSIG) input[12 + 2]) << 16 | ((XQWORDSIG) input[12 + 3]) << 24) >> 6) & 0x3ffffff;;
00714   output[5] = ((((XQWORDSIG) input[16 + 0]) | ((XQWORDSIG) input[16 + 1]) << 8 | ((XQWORDSIG) input[16 + 2]) << 16 | ((XQWORDSIG) input[16 + 3]) << 24) >> 0) & 0x1ffffff;;
00715   output[6] = ((((XQWORDSIG) input[19 + 0]) | ((XQWORDSIG) input[19 + 1]) << 8 | ((XQWORDSIG) input[19 + 2]) << 16 | ((XQWORDSIG) input[19 + 3]) << 24) >> 1) & 0x3ffffff;;
00716   output[7] = ((((XQWORDSIG) input[22 + 0]) | ((XQWORDSIG) input[22 + 1]) << 8 | ((XQWORDSIG) input[22 + 2]) << 16 | ((XQWORDSIG) input[22 + 3]) << 24) >> 3) & 0x1ffffff;;
00717   output[8] = ((((XQWORDSIG) input[25 + 0]) | ((XQWORDSIG) input[25 + 1]) << 8 | ((XQWORDSIG) input[25 + 2]) << 16 | ((XQWORDSIG) input[25 + 3]) << 24) >> 4) & 0x3ffffff;;
00718   output[9] = ((((XQWORDSIG) input[28 + 0]) | ((XQWORDSIG) input[28 + 1]) << 8 | ((XQWORDSIG) input[28 + 2]) << 16 | ((XQWORDSIG) input[28 + 3]) << 24) >> 6) & 0x1ffffff;;
00719 }
