00721 
00734 void CIPHERCURVE25519::fcontract(XBYTE* output, XQWORDSIG* input) 
00735 {
00736   int i;
00737   int j;
00738 
00739   for(j=0; j<2; ++j) 
00740     {
00741       for(i=0; i<9; ++i) 
00742         {
00743           if((i & 1) == 1) 
00744             {
00745               // This calculation is a time-invariant way to make input[i] positive by borrowing from the next-larger XQWORDSIG.        
00746               
00747               const XDWORDSIG mask  = (XDWORDSIG)(input[i]) >> 31;
00748               const XDWORDSIG carry = -(((XDWORDSIG)(input[i]) & mask) >> 25);
00749         
00750               input[i]    = (XDWORDSIG)(input[i]) + (carry << 25);
00751               input[i+1]  = (XDWORDSIG)(input[i+1]) - carry;
00752 
00753             } 
00754            else 
00755             {
00756               const XDWORDSIG mask  = (XDWORDSIG)(input[i]) >> 31;
00757               const XDWORDSIG carry = -(((XDWORDSIG)(input[i]) & mask) >> 26);
00758         
00759               input[i]    = (XDWORDSIG)(input[i]) + (carry << 26);
00760               input[i+1]  = (XDWORDSIG)(input[i+1]) - carry;
00761             }
00762         }
00763     
00764       const XDWORDSIG mask  = (XDWORDSIG)(input[9]) >> 31;
00765       const XDWORDSIG carry = -(((XDWORDSIG)(input[9]) & mask) >> 25);
00766 
00767       input[9] = (XDWORDSIG)(input[9]) + (carry << 25);
00768       input[0] = (XDWORDSIG)(input[0]) - (carry * 19);
00769     }
00770 
00771   // The first borrow-propagation pass above ended with every XQWORDSIG except (possibly) input[0] non-negative.
00772   //
00773   // Since each input XQWORDSIG except input[0] is decreased by at most 1 by a borrow-propagation pass, the second borrow-propagation pass
00774   // could only have wrapped around to decrease input[0] again if the first pass left input[0] negative *and* input[1] through input[9]
00775   // were all zero.  In that case, input[1] is now 2^25 - 1, and this last borrow-propagation step will leave input[1] non-negative.
00776   
00777   const XDWORDSIG mask  =  (XDWORDSIG)(input[0]) >> 31;
00778   const XDWORDSIG carry = -(((XDWORDSIG)(input[0]) & mask) >> 26);
00779 
00780   input[0] = (XDWORDSIG)(input[0]) + (carry << 26);
00781   input[1] = (XDWORDSIG)(input[1]) - carry;
00782 
00783   // Both passes through the above loop, plus the last 0-to-1 step, are necessary: if input[9] is -1 and input[0] through input[8] are 0,
00784   //   negative values will remain in the array until the end.
00785    
00786 
00787   input[1] <<= 2;
00788   input[2] <<= 3;
00789   input[3] <<= 5;
00790   input[4] <<= 6;
00791   input[6] <<= 1;
00792   input[7] <<= 3;
00793   input[8] <<= 4;
00794   input[9] <<= 6;
00795 
00796   output[   0]  = 0;
00797   output[  16]  = 0;
00798 
00799   output[ 0+0] |= input[0] & 0xff; output[ 0+1] = (input[0] >> 8) & 0xff; output[ 0+2] = (input[0] >> 16) & 0xff; output[ 0+3] = (input[0] >> 24) & 0xff;;
00800   output[ 3+0] |= input[1] & 0xff; output[ 3+1] = (input[1] >> 8) & 0xff; output[ 3+2] = (input[1] >> 16) & 0xff; output[ 3+3] = (input[1] >> 24) & 0xff;;
00801   output[ 6+0] |= input[2] & 0xff; output[ 6+1] = (input[2] >> 8) & 0xff; output[ 6+2] = (input[2] >> 16) & 0xff; output[ 6+3] = (input[2] >> 24) & 0xff;;
00802   output[ 9+0] |= input[3] & 0xff; output[ 9+1] = (input[3] >> 8) & 0xff; output[ 9+2] = (input[3] >> 16) & 0xff; output[ 9+3] = (input[3] >> 24) & 0xff;;
00803   output[12+0] |= input[4] & 0xff; output[12+1] = (input[4] >> 8) & 0xff; output[12+2] = (input[4] >> 16) & 0xff; output[12+3] = (input[4] >> 24) & 0xff;;
00804   output[16+0] |= input[5] & 0xff; output[16+1] = (input[5] >> 8) & 0xff; output[16+2] = (input[5] >> 16) & 0xff; output[16+3] = (input[5] >> 24) & 0xff;;
00805   output[19+0] |= input[6] & 0xff; output[19+1] = (input[6] >> 8) & 0xff; output[19+2] = (input[6] >> 16) & 0xff; output[19+3] = (input[6] >> 24) & 0xff;;
00806   output[22+0] |= input[7] & 0xff; output[22+1] = (input[7] >> 8) & 0xff; output[22+2] = (input[7] >> 16) & 0xff; output[22+3] = (input[7] >> 24) & 0xff;;
00807   output[25+0] |= input[8] & 0xff; output[25+1] = (input[8] >> 8) & 0xff; output[25+2] = (input[8] >> 16) & 0xff; output[25+3] = (input[8] >> 24) & 0xff;;
00808   output[28+0] |= input[9] & 0xff; output[28+1] = (input[9] >> 8) & 0xff; output[28+2] = (input[9] >> 16) & 0xff; output[28+3] = (input[9] >> 24) & 0xff;;
00809 }
