00831 void CIPHERCURVE25519::fmonty(XQWORDSIG* x2, XQWORDSIG* z2, XQWORDSIG* x3, XQWORDSIG* z3, XQWORDSIG* x, XQWORDSIG* z, XQWORDSIG* xprime, XQWORDSIG* zprime, const XQWORDSIG* qmqp) 
00832 {
00833   XQWORDSIG origx[10];
00834   XQWORDSIG origxprime[10];
00835   XQWORDSIG zzz[19];
00836   XQWORDSIG xx[19]; 
00837   XQWORDSIG zz[19];
00838   XQWORDSIG xxprime[19];
00839   XQWORDSIG zzprime[19];
00840   XQWORDSIG zzzprime[19]; 
00841   XQWORDSIG xxxprime[19];
00842 
00843   memcpy(origx, x, 10 * sizeof(XQWORDSIG));
00844   fsum(x, z);
00845   fdifference(z, origx);                          // does x - z
00846 
00847   memcpy(origxprime, xprime, sizeof(XQWORDSIG) * 10);
00848   fsum(xprime, zprime);
00849   fdifference(zprime, origxprime);
00850   fproduct(xxprime, xprime, z);
00851   fproduct(zzprime, x, zprime);
00852   freduce_degree(xxprime);
00853   freduce_coefficients(xxprime);
00854   freduce_degree(zzprime);
00855   freduce_coefficients(zzprime);
00856   memcpy(origxprime, xxprime, sizeof(XQWORDSIG) * 10);
00857   fsum(xxprime, zzprime);
00858   fdifference(zzprime, origxprime);
00859   fsquare(xxxprime, xxprime);
00860   fsquare(zzzprime, zzprime);
00861   fproduct(zzprime, zzzprime, qmqp);
00862   freduce_degree(zzprime);
00863   freduce_coefficients(zzprime);
00864   memcpy(x3, xxxprime, sizeof(XQWORDSIG) * 10);
00865   memcpy(z3, zzprime, sizeof(XQWORDSIG) * 10);
00866 
00867   fsquare(xx, x);
00868   fsquare(zz, z);
00869   fproduct(x2, xx, zz);
00870   freduce_degree(x2);
00871   freduce_coefficients(x2);
00872   fdifference(zz, xx);                            // does zz = xx - zz
00873   memset(zzz + 10, 0, sizeof(XQWORDSIG) * 9);
00874   fscalar_product(zzz, zz, 121665);
00875 
00876   // No need to call freduce_degree here: fscalar_product doesn't increase the degree of its input. 
00877   freduce_coefficients(zzz);
00878   fsum(zzz, xx);
00879   fproduct(z2, zz, zzz);
00880   freduce_degree(z2);
00881   freduce_coefficients(z2);
00882 }
