00905 void CIPHERCURVE25519::swap_conditional(XQWORDSIG a[19], XQWORDSIG b[19], XQWORDSIG iswap) 
00906 {
00907   const XDWORDSIG swap = (XDWORDSIG)-iswap;  
00908   XDWORD i;
00909 
00910   for(i=0; i<10; ++i) 
00911     {
00912       const XDWORDSIG x = swap & ( ((XDWORDSIG)a[i]) ^ ((XDWORDSIG)b[i]) );
00913 
00914       a[i] = ((XDWORDSIG)a[i]) ^ x;
00915       b[i] = ((XDWORDSIG)b[i]) ^ x;
00916     }
00917 }
