00937 void CIPHERCURVE25519::cmult(XQWORDSIG* resultx, XQWORDSIG* resultz, const XBYTE* n, const XQWORDSIG* q) 
00938 {
00939   XQWORDSIG   a[19]   = {0};  
00940   XQWORDSIG   b[19]   = {1};  
00941   XQWORDSIG   c[19]   = {1};  
00942   XQWORDSIG   d[19]   = {0};
00943   XQWORDSIG*  nqpqx   = a;
00944   XQWORDSIG*  nqpqz   = b; 
00945   XQWORDSIG*  nqx     = c;
00946   XQWORDSIG*  nqz     = d;
00947   XQWORDSIG*  t;
00948   XQWORDSIG   e[19]   = {0}; 
00949   XQWORDSIG   f[19]   = {1}; 
00950   XQWORDSIG   g[19]   = {0}; 
00951   XQWORDSIG   h[19]   = {1};
00952   XQWORDSIG*  nqpqx2  = e;
00953   XQWORDSIG*  nqpqz2  = f;
00954   XQWORDSIG*  nqx2    = g;
00955   XQWORDSIG*  nqz2    = h;
00956   XDWORD    i;
00957   XDWORD    j;
00958 
00959   memcpy(nqpqx, q, sizeof(XQWORDSIG) * 10);
00960 
00961   for(i=0; i<32; ++i) 
00962     {
00963       XBYTE byte = n[31 - i];
00964       for(j=0; j<8; ++j) 
00965         {
00966           const XQWORDSIG bit = byte >> 7;
00967 
00968           swap_conditional(nqx, nqpqx, bit);
00969           swap_conditional(nqz, nqpqz, bit);
00970           fmonty(nqx2, nqz2, nqpqx2, nqpqz2, nqx, nqz, nqpqx, nqpqz, q);
00971           swap_conditional(nqx2, nqpqx2, bit);
00972           swap_conditional(nqz2, nqpqz2, bit);
00973 
00974           t       = nqx;
00975           nqx     = nqx2;
00976           nqx2    = t;
00977           t       = nqz;
00978           nqz     = nqz2;
00979           nqz2    = t;
00980           t       = nqpqx;
00981           nqpqx   = nqpqx2;
00982           nqpqx2  = t;
00983           t       = nqpqz;
00984           nqpqz   = nqpqz2;
00985           nqpqz2  = t;
00986 
00987           byte  <<= 1;
00988         }
00989     }
00990 
00991   memcpy(resultx, nqx, sizeof(XQWORDSIG) * 10);
00992   memcpy(resultz, nqz, sizeof(XQWORDSIG) * 10);
00993 }
