00995 
01008 void CIPHERCURVE25519::crecip(XQWORDSIG* out, const XQWORDSIG* z) 
01009 {
01010   XQWORDSIG z2[10];
01011   XQWORDSIG z9[10];
01012   XQWORDSIG z11[10];
01013   XQWORDSIG z2_5_0[10];
01014   XQWORDSIG z2_10_0[10];
01015   XQWORDSIG z2_20_0[10];
01016   XQWORDSIG z2_50_0[10];
01017   XQWORDSIG z2_100_0[10];
01018   XQWORDSIG t0[10];
01019   XQWORDSIG t1[10];
01020   int       i;
01021 
01022   fsquare(z2,z);                    // 2 
01023   fsquare(t1,z2);                   // 4 
01024   fsquare(t0,t1);                   // 8 
01025   fmul(z9,t0,z);                    // 9 
01026   fmul(z11,z9,z2);                  // 11 
01027   fsquare(t0,z11);                  // 22  
01028   fmul(z2_5_0,t0,z9);               // 2^5 - 2^0 = 31 
01029 
01030   fsquare(t0,z2_5_0);               // 2^6 - 2^1  
01031   fsquare(t1,t0);                   // 2^7 - 2^2  
01032   fsquare(t0,t1);                   // 2^8 - 2^3 
01033   fsquare(t1,t0);                   // 2^9 - 2^4  
01034   fsquare(t0,t1);                   // 2^10 - 2^5 
01035   fmul(z2_10_0,t0,z2_5_0);          // 2^10 - 2^0 
01036 
01037   fsquare(t0,z2_10_0);              // 2^11 - 2^1 
01038   fsquare(t1,t0);                   // 2^12 - 2^2 
01039   for(i=2; i<10; i+=2)              // 2^20 - 2^10   
01040     { 
01041       fsquare(t0,t1); fsquare(t1,t0); 
01042     }
01043   fmul(z2_20_0,t1,z2_10_0);         // 2^20 - 2^0 
01044 
01045   fsquare(t0,z2_20_0);              // 2^21 - 2^1 
01046   fsquare(t1,t0);                   // 2^22 - 2^2 
01047   for (i=2; i<20; i+=2)             // 2^40 - 2^20 
01048     { fsquare(t0,t1); fsquare(t1,t0); 
01049     }
01050   fmul(t0,t1,z2_20_0);              // 2^40 - 2^0 
01051 
01052   fsquare(t1,t0);                   // 2^41 - 2^1 
01053   fsquare(t0,t1);                   // 2^42 - 2^2  
01054   for(i=2; i<10; i+=2)              // 2^50 - 2^10  
01055   { 
01056     fsquare(t1,t0); fsquare(t0,t1); 
01057   }
01058   fmul(z2_50_0,t0,z2_10_0);         // 2^50 - 2^0 
01059 
01060   fsquare(t0,z2_50_0);              // 2^51 - 2^1 
01061   fsquare(t1,t0);                   // 2^52 - 2^2 
01062   for(i=2; i<50; i+=2)              // 2^100 - 2^50  
01063     { 
01064       fsquare(t0,t1); fsquare(t1,t0); 
01065     }
01066   fmul(z2_100_0,t1,z2_50_0);        // 2^100 - 2^0  
01067 
01068   fsquare(t1,z2_100_0);             // 2^101 - 2^1 
01069   fsquare(t0,t1);                   // 2^102 - 2^2 
01070   for(i=2; i<100; i+=2)             // 2^200 - 2^100   
01071     { 
01072       fsquare(t1,t0); fsquare(t0,t1); 
01073     }
01074   fmul(t1,t0,z2_100_0);             // 2^200 - 2^0 
01075 
01076   fsquare(t0,t1);                   // 2^201 - 2^1 
01077   fsquare(t1,t0);                   // 2^202 - 2^2 
01078   for(i=2; i<50; i+=2)              // 2^250 - 2^50 
01079     { 
01080       fsquare(t0,t1); fsquare(t1,t0); 
01081     }
01082   fmul(t0,t1,z2_50_0);              // 2^250 - 2^0 
01083 
01084   fsquare(t1,t0);                   // 2^251 - 2^1 
01085   fsquare(t0,t1);                   // 2^252 - 2^2 
01086   fsquare(t1,t0);                   // 2^253 - 2^3 
01087   fsquare(t0,t1);                   // 2^254 - 2^4 
01088   fsquare(t1,t0);                   // 2^255 - 2^5 
01089   fmul(out,t1,z11);                 // 2^255 - 21 
01090 }
