01092 
01106 void CIPHERCURVE25519::curve25519(XBYTE* mypublic, const XBYTE* secret, const XBYTE* basepoint) 
01107 {
01108   XQWORDSIG bp[10];
01109   XQWORDSIG x[10];
01110   XQWORDSIG z[11]; 
01111   XQWORDSIG zmone[10];
01112   XBYTE     e[CIPHERCURVE25519_MAXKEY];
01113   int       i;
01114 
01115   if(basepoint == 0) 
01116     {
01117       basepoint = CIPHERCURVE25519::basepoint;
01118     }
01119 
01120   for(i = 0; i < CIPHERCURVE25519_MAXKEY; ++i) 
01121     { 
01122       e[i] = secret[i];
01123     }
01124 
01125   e[0]  &= 248;
01126   e[31] &= 127;
01127   e[31] |= 64;
01128 
01129   fexpand(bp, basepoint);
01130   cmult(x, z, e, bp);
01131   crecip(zmone, z);
01132   fmul(z, x, zmone);
01133   freduce_coefficients(z);
01134   fcontract(mypublic, z);
01135 }
