00484 bool DIOAIOBIMAINBOARD::SetDigitalOutput(XBYTE bit, bool on)
00485 {
00486   if(!pcf8574[DIOAIOBIMAINBOARD_I2CDIGIGPIO_OUT])                           return false;
00487   if(!pcf8574[DIOAIOBIMAINBOARD_I2CDIGIGPIO_OUT]->IsInitialized())          return false;
00488 
00489   XBYTE mask = 0x01;
00490 
00491   if(bit-1)
00492     {
00493       mask <<= (bit-1);
00494     }
00495 
00496   if(on)
00497         outputdigitaldata |=  mask;
00498    else outputdigitaldata &= ~mask;
00499 
00500   return pcf8574[DIOAIOBIMAINBOARD_I2CDIGIGPIO_OUT]->Write(outputdigitaldata);
00501 }
