00648 bool DIOAIOBIMAINBOARD::ResetMicroControler()
00649 {
00650   if(!SetModeHeaderPin(DIOAIOBIMAINBOARD_HEADER_SLOT,  9, false)) return false;   // Reset Micro
00651   if(!SetStatusHeaderPin(DIOAIOBIMAINBOARD_HEADER_SLOT, 9, true)) return false;
00652 
00653   bool pinstatus = false;
00654 
00655   if(SetStatusHeaderPin(DIOAIOBIMAINBOARD_HEADER_SLOT, 9,  pinstatus))
00656     {
00657       GEN_XSLEEP.MilliSeconds(10);
00658 
00659       pinstatus = !pinstatus;
00660 
00661       return SetStatusHeaderPin(DIOAIOBIMAINBOARD_HEADER_SLOT, 9,  pinstatus);
00662     }
00663 
00664   return false;
00665 }
