00677 bool DIOAIOBIMAINBOARD::End()
00678 {
00679   for(int c=0; c<3; c++)
00680     {
00681       if(pcf8574[c])
00682         {
00683           pcf8574[c]->End();
00684 
00685           delete pcf8574[c];
00686           pcf8574[c] = NULL;
00687         }
00688     }
00689 
00690   if(eeprom)
00691     {
00692       eeprom->End();
00693       delete eeprom;
00694       eeprom = NULL;
00695     }
00696 
00697 
00698   if(pca9685)
00699     {
00700       pca9685->GetDIOStream()->WaitToFlushXBuffers(3);
00701       pca9685->End();
00702       delete pca9685;
00703       pca9685 = NULL;
00704     }
00705 
00706   if(gpio)
00707     {
00708       SetLedA(false);
00709       SetLedB(false);
00710 
00711       gpio->End();
00712       GEN_DIOFACTORY.DeleteGPIO(gpio);
00713       gpio = NULL;
00714     }
00715 
00716   return true;
00717 }
