00293 int DIOANDROIDSTREAMBLUETOOTHREMOTEENUMDEVICES::IsReadyConnect(int socket)
00294 {
00295   struct timeval  tv;
00296   int             rc;
00297   fd_set          fdr;
00298   fd_set          fdw;
00299   fd_set          fds;
00300 
00301   if(socket==-1) return -1;
00302 
00303   FD_ZERO(&fdr);
00304   FD_ZERO(&fdw);
00305   FD_ZERO(&fds);
00306 
00307   FD_SET((unsigned int)socket,&fdr);
00308   FD_SET((unsigned int)socket,&fdw);
00309   FD_SET((unsigned int)socket,&fds);
00310 
00311   tv.tv_sec  = 0;
00312   tv.tv_usec = 100;
00313 
00314   rc = select(socket+1, &fdr, &fdw, &fds, &tv);
00315   if(rc==-1) return -1;
00316   if(rc== 2) return -1;
00317 
00318   int status1 = FD_ISSET(socket,&fdr) ? 1 : 0;
00319   int status2 = FD_ISSET(socket,&fdw) ? 1 : 0;
00320   int status3 = FD_ISSET(socket,&fds) ? 1 : 0;
00321 
00322   if(status3) return -1;
00323   if(((status1) || (status2)) && (rc==1))  return  1;
00324 
00325   return 0;
