00226 bool DIOANDROIDSTREAMUART::Config(XWORD mask)
00227 {
00228   if(fd<0) return false;
00229 
00230   struct termios cfg;
00231   speed_t        speed;
00232 
00233   tcgetattr(fd, &cfg);      // save current port settings
00234 
00235   if((mask&DIOSTREAMUARTMASK_BAUDRATE) == DIOSTREAMUARTMASK_BAUDRATE)
00236     {
00237       switch(this->config->GetBaudRate())
00238         {
00239           case 50     : speed = B50;        break;
00240           case 300    : speed = B300;       break;
00241           case 600    : speed = B600;       break;
00242           case 1200   : speed = B1200;      break;
00243           case 4800   : speed = B4800;      break;
00244           case 9600   : speed = B9600;      break;
00245           case 19200  : speed = B19200;     break;
00246           case 38400  : speed = B38400;     break;
00247           case 57600  : speed = B57600;     break;
00248           case 115200 : speed = B115200;    break;
00249         }
00250 
00251       cfsetispeed(&cfg, speed);
00252       cfsetospeed(&cfg, speed);
00253     }
00254 
00255   if((mask&DIOSTREAMUARTMASK_DATABITS ) == DIOSTREAMUARTMASK_DATABITS)
00256     {
00257       cfg.c_cflag&= ~CSIZE;
00258 
00259       switch(this->config->GetDataBits())
00260         {
00261           case DIOSTREAMUARTDATABIT_5           : cfg.c_cflag|= CS5; break;
00262           case DIOSTREAMUARTDATABIT_6           : cfg.c_cflag|= CS6; break;
00263           case DIOSTREAMUARTDATABIT_7           : cfg.c_cflag|= CS7; break;
00264           case DIOSTREAMUARTDATABIT_8           : cfg.c_cflag|= CS8; break;
00265         }
00266     }
00267 
00268   if((mask&DIOSTREAMUARTMASK_PARITY) == DIOSTREAMUARTMASK_PARITY)
00269     {
00270       cfg.c_iflag  = (IGNBRK| IGNPAR);
00271 
00272       cfg.c_cflag &= ~CMSPAR;
00273       cfg.c_cflag &= ~PARENB;
00274       cfg.c_cflag &= ~PARODD;
00275 
