00279                                                   cfg.c_cflag &= ~PARENB;
00280                                                   cfg.c_cflag &= ~PARODD;
00281                                                   break;
00282 
00283           case DIOSTREAMUARTPARITY_MARK         : cfg.c_cflag |= CMSPAR;       // Not available on many systems.
00284 
00285           case DIOSTREAMUARTPARITY_ODD          : cfg.c_cflag |= PARENB;
00286                                                   cfg.c_cflag |= PARODD;
00287                                                   break;
00288 
