00303           case DIOSTREAMUARTSTOPBITS_ONE          : cfg.c_cflag &= ~CSTOPB;   break;
00304           case DIOSTREAMUARTSTOPBITS_ONEANDAHALF  :
00305           case DIOSTREAMUARTSTOPBITS_TWO          : cfg.c_cflag |=  CSTOPB;   break;
00306         }
00307     }
00308 
00309   if((mask&DIOSTREAMUARTMASK_FLOWCONTROL) == DIOSTREAMUARTMASK_FLOWCONTROL)
00310     {
00311       switch(config->GetFlowControl())
00312         {
00313           case DIOSTREAMUARTFLOWCONTROL_NONE    : cfg.c_cflag &= ~CRTSCTS;
00314                                                   cfg.c_iflag &= ~(IXON | IXOFF | IXANY);
00315                                                   break;
00316 
00317           case DIOSTREAMUARTFLOWCONTROL_HARD    : cfg.c_cflag |=  CRTSCTS;
00318                                                   cfg.c_iflag &= ~(IXON | IXOFF | IXANY);
00319                                                   break;
00320 
00321           case DIOSTREAMUARTFLOWCONTROL_SOFT    : cfg.c_cflag &= ~CRTSCTS;
00322                                                   cfg.c_iflag |= (IXON | IXOFF | IXANY);
