00324         }
00325     }
00326 
00327   if((mask&DIOSTREAMUARTMASK_ALL) == DIOSTREAMUARTMASK_ALL)
00328     {
00329 
00330       cfg.c_cflag |= (CLOCAL | CREAD);
00331 
00332       cfg.c_oflag &= ~OPOST;
