00334 
00335 
00336       cfg.c_cc[VMIN]  = 0;      // 0 means use-vtime
00337       cfg.c_cc[VTIME] = 1;      // time to wait until
00338     }
00339 
00340   tcsetattr(fd, TCSANOW, &cfg);
