00568 bool DIOANDROIDSTREAMUART::SetRTS(bool on)
00569 {
00570   if(fd<0) return false;
00571 
00572   int _status;
00573 
00574   ioctl(fd, TIOCMGET, &_status); // get the serial port status
00575 
00576   if(on) _status &= ~TIOCM_RTS; else _status |= TIOCM_RTS;
00577 
00578   ioctl(fd, TIOCMSET, &_status); // set the serial port status
00579 
00580   return true;
00581 }
