00642 void DIOANDROIDSTREAMUART::ThreadConnection(void* param)
00643 {
00644   DIOANDROIDSTREAMUART* diostream = (DIOANDROIDSTREAMUART*)param;
00645   if(!diostream) return;
00646 
00647   if(diostream->GetEvent()==DIOANDROIDUARTFSMEVENT_NONE) // No hay nuevos Eventos
00648     {
00649       switch(diostream->GetCurrentState())
00650         {
00651           case DIOANDROIDUARTFSMSTATE_NONE                    : break;
00652 
00653           case DIOANDROIDUARTFSMSTATE_CONNECTED               : break;
00654 
00655           case DIOANDROIDUARTFSMSTATE_WAITINGTOREAD           : {
00656                                                                   if(!diostream->IsBlockRead())
00657                                                                     {
00658                                                                       XBYTE buffer[DIOSTREAM_MAXBUFFER];
00659 
00660                                                                       XDWORD br = diostream->ReadDirect(buffer, DIOSTREAM_MAXBUFFER);
00661                                                                       if(br)
00662                                                                         {
00663                                                                           //XTRACE_PRINTDATABLOCK(XDEBUGLEVELCOLOR(2), (XBYTE*)diostream->buffer, (int)br);
00664                                                                           diostream->inbuffer->Add(buffer, br);
00665                                                                         }
00666                                                                     }
00667 
00668                                                                   if(!diostream->IsBlockWrite())
00669                                                                     {
00670                                                                       int esize = diostream->outbuffer->GetSize();
00671                                                                       if(esize)
00672                                                                         {
00673                                                                           if(esize>DIOSTREAM_MAXBUFFER) esize = DIOSTREAM_MAXBUFFER;
00674 
00675                                                                           diostream->outbuffer->SetBlocked(true);
00676                                                                           XDWORD bw = diostream->WriteDirect(diostream->outbuffer->Get(), esize);
00677                                                                           diostream->outbuffer->SetBlocked(false);
00678 
00679                                                                           if(bw)
00680                                                                             {
00681                                                                               //XTRACE_PRINTDATABLOCK(XDEBUGLEVELCOLOR(4), (XBYTE*)diostream->buffer, (int)bw);
00682                                                                               diostream->outbuffer->Extract(NULL, 0, bw);
00683                                                                             }
00684                                                                         }
00685                                                                     }
00686                                                               }
00687                                                               break;
00688 
00689           case DIOANDROIDUARTFSMSTATE_DISCONNECTING           : break;
00690 
00691         }
00692     }
00693    else
00694     {
00695       if(diostream->GetEvent()<DIOANDROIDUART_LASTEVENT)
00696         {
00697           diostream->CheckTransition();
00698 
00699           switch(diostream->GetCurrentState())
00700             {
00701               case DIOANDROIDUARTFSMSTATE_NONE                : break;
00702 
00703               case DIOANDROIDUARTFSMSTATE_CONNECTED           : { DIOSTREAMXEVENT xevent(diostream,DIOSTREAMXEVENT_TYPE_CONNECTED);
00704                                                                 xevent.SetDIOStream(diostream);
00705                                                                 diostream->PostEvent(&xevent);
00706 
00707                                                                 diostream->SetEvent(DIOANDROIDUARTFSMEVENT_WAITINGTOREAD);
00708                                                               }
00709                                                               break;
00710 
00711               case DIOANDROIDUARTFSMSTATE_WAITINGTOREAD       : break;
00712 
00713               case DIOANDROIDUARTFSMSTATE_DISCONNECTING       : { DIOSTREAMXEVENT xevent(diostream,DIOSTREAMXEVENT_TYPE_DISCONNECTED);
00714                                                                 xevent.SetDIOStream(diostream);
00715                                                                 diostream->PostEvent(&xevent);
00716 
00717                                                                 diostream->threadconnection->Run(false);
00718                                                                 diostream->status = DIOSTREAMSTATUS_DISCONNECTED;
00719                                                               }
00720                                                               break;
00721             }
00722         }
00723     }
00724 }
