00201 bool DIOANDROIDSTREAMUDP::Disconnect()
00202 {
00203   if((GetConnectStatus()==DIOSTREAMSTATUS_GETTINGCONNECTION)||
00204      (GetConnectStatus()==DIOSTREAMSTATUS_CONNECTED))
00205      {
00206        SetEvent(DIOANDROIDUDPFSMEVENT_DISCONNECTING);
00207 
00208        while(GetConnectStatus()!=DIOSTREAMSTATUS_DISCONNECTED)
00209         {
00210           usleep(10000);
00211         }
00212      }
