00255 int DIOANDROIDSTREAMUDP::IsReadyConnect(int socket)
00256 {
00257   struct timeval  tv;
00258   int             rc;
00259   fd_set          fdr;
00260   fd_set          fdw;
00261   fd_set          fds;
00262 
00263   if(socket==-1) return -1;
00264 
00265   FD_ZERO(&fdr);
00266   FD_ZERO(&fdw);
00267   FD_ZERO(&fds);
00268 
00269   FD_SET(socket,&fdr);
00270   FD_SET(socket,&fdw);
00271   FD_SET(socket,&fds);
00272 
00273   tv.tv_sec  = 0;
00274   tv.tv_usec = 100;
00275 
00276   rc = select(socket+1, &fdr, &fdw, &fds, &tv);
00277   if(rc==-1) return -1;
00278 
00279   int status1 = FD_ISSET(socket,&fdr) ? 1 : 0;
00280   int status2 = FD_ISSET(socket,&fdw) ? 1 : 0;
00281   int status3 = FD_ISSET(socket,&fds) ? 1 : 0;
00282 
00283   if(config->IsServer())
00284     {
00285       if(status1 || status2 ) return  1;
00286       if(status3)             return -1;
00287     }
00288    else
00289     {
00290       if((!status1) && status2) return  1;
00291       if(status3)               return -1;
00292     }
00293 
00294   return 0;
00295 }
