00165 XWORD DIOATCMDGSM_ALPHABET_8859_1::Traslate(bool from, XWORD data)
00166 {
00167   XDWORD end         = (sizeof(table)/sizeof(char[2]));
00168   char  extended   = ((data&0xFF00)>>8);
00169   char  character  = (data&0x00FF);
00170   char  escape     = 0x1B;
00171   XDWORD c;
00172 
00173   if(from)
00174     {
00175       for(c=0;c<127;c++)
00176         {
00177           if(table[c][1]==character) return (XWORD)table[c][0];
00178         }
00179 
00180       for(c=128;c<end;c++)
00181         {
00182           if(table[c][1]==character) return (XWORD)((escape<<8)|table[c][0]);
00183         }
00184     }
00185    else
00186     {
00187       int first =0;
00188 
00189       if(extended==escape)
00190             first = 128;
00191        else end  = 127;
00192 
00193       for(c=first;c<end;c++)
00194         {
00195           if(table[c][0]==character) return (XWORD)table[c][1];
00196         }
00197      }
00198 
00199   return 0xFF00;
00200 }
