00900   int                          value;
00901   XBYTE*                       codec7 = new XBYTE[256];
00902   XBYTE*                       codec8 = new XBYTE[256];
00903   int                          size7  = 0;
00904   int                          size8  = 0;
00905   int                          c;
00906 
00907   if((!codec7) || (!codec8)) return false;
00908 
00909   memset(codec7 ,0 , 256);
00910   memset(codec8 ,0 , 256);
00911 
00912   result.Empty();
00913 
00914   _number  = number;
00915   _message = message;
00916 
00917   // No SMS Center.
00918   result += __L("00");
00919   // SMS-SUBMIT mensaje.
00920   result += __L("11");
00921   result += __L("00");
00922 
00923   // Size Number (National)
00924   string.Format(__L("%02X"),_number.GetSize());
00925   result += string;
00926 
00927   if(_number.GetSize()>9)
00928          result += __L("91");    // Type Number (International);
00929     else result += __L("81");    // Type Number (National);
00930 
00931   // GSM Number
00932   string = number;
00933   if(string.GetSize()%2) string += __L("F");
00934   string.Swab();
00935   result += string;
00936   result += __L("00");
00937 
00938   // Codec 7/8 Bits
00939   if(inoctets)
00940          result += __L("02");
00941     else result += __L("00");
00942 
00943   // Time Max Msg <28 days  >28 weeks
00944   if(validperiod>28)
00945           value = 168 + validperiod;
00946     else  value = 197 + validperiod;
00947   string.Format(__L("%02X"),value);
00948   result += string;
00949 
00950   // Convert message
00951   for(c=0;c<(int)_message.GetSize();c++)
00952     {
00953       XWORD data=alphabet.Traslate(false,(XBYTE)message[c]);
00954       XBYTE byte[2];
00955 
00956       byte[1]=(XBYTE)(data>>8);
00957       byte[0]=(XBYTE)(data&0x00FF);
00958 
00959       if(byte[1])
00960         {
00961           codec8[size8]=byte[1];
00962           size8++;
00963         }
00964 
00965       codec8[size8]=byte[0];
00966       size8++;
00967     }
00968 
00969 
00970   // Compress 7 Bits.
00971   if(!inoctets)
00972     {
00973       int shift=0;
00974 
00975       for(c=0;c<size8;c++)
00976         {
00977           XBYTE mask = (0xFF>>(7-shift));
00978           XBYTE data = (codec8[c+1]&mask)<<(XBYTE)(7-shift);
00979 
00980           if(shift!=7)
00981             {
00982               codec7[size7]=(data|(codec8[c]>>(XBYTE)shift));
00983               size7++;
00984             }
00985 
00986           shift++;
00987           if(shift>7) shift=0;
00988         }
00989     }
00990 
00991   string.Format(__L("%02X"),_message.GetSize());
00992   result += string;
00993 
00994   for(c=0;c<((inoctets)?size8:size7);c++)
00995     {
00996       string.Format(__L("%02X"),(inoctets)?codec8[c]:codec7[c]);
00997       result += string;
00998     }
00999 
01000   delete [] codec7;
01001   delete [] codec8;
01002 
01003   length.Format(__L("%d"),(result.GetSize()/2)-1);
01004 
01005   return true;
01006 }
