01419 int DIOATCMDS::SearchInAnswers(XCHAR* string, int timeout)
01420 {
01421   if(timeout) xtimer->Reset();
01422 
01423   while(1)
01424     {
01425       for(int c=0;c<(int)answers.GetSize();c++)
01426         {
01427           XSTRING* answer = (XSTRING*)answers.Get(c);
01428           if(answer)
01429             {
01430               if(answer->Find(string,true)!=XSTRING_NOTFOUND) return c;
01431             }
01432         }
01433 
01434       if(timeout)
01435         {
01436           if((int)xtimer->GetMeasureSeconds()>=timeout)
01437             {
01438               return -1;
01439 
01440             } else GEN_XSLEEP.MilliSeconds(10);
01441 
01442         } else break;
01443 
01444     }
01445 
01446   return DIOATCMDS_ANSWERNOTFOUND;
01447 }
