01449 
01462 int DIOATCMDS::SearchInAnswers(XDWORD typecommand,int timeout)
01463 {
01464   DIOATCMD* command = GetCommand(typecommand);
01465   if(!command) return DIOATCMDS_ANSWERNOTFOUND;
01466 
01467   XSTRING string;
01468 
01469   string = command->GetCommand();
01470   string += __L(":");
01471 
01472   return SearchInAnswers(string.Get(),timeout);
01473 }
