01475 
01488 DIOATCMD_ERROR DIOATCMDS::WaitToProcessAnswer(int timeout, int* index)
01489 {
01490   if(!xtimer) return DIOATCMD_ERROR_UNKNOWN;
01491 
01492   xtimer->Reset();
01493 
01494   do{ for(int c=0;c<(int)answers.GetSize();c++)
01495         {
01496           XSTRING* answer = (XSTRING*)answers.Get(c);
01497           if(answer)
01498             {
01499               if((!answer->Compare(__L("OK")))  || (!answer->Compare(__L("0"))))
01500                 {
01501                   if(index) (*index) = c;
01502                   DeleteAnswer(c);
01503                   return DIOATCMD_ERROR_NONE;
01504                 }
01505 
01506               if((!answer->Compare(__L("ERROR")))  || (!answer->Compare(__L("1"))))
01507                 {
01508                   if(index) (*index) = c;
01509                   DeleteAnswer(c);
01510                   return DIOATCMD_ERROR_INVALIDCMD;
01511                 }
01512             }
01513         }
01514 
01515       if((int)xtimer->GetMeasureSeconds()>=timeout)
01516         {
01517           return DIOATCMD_ERROR_TIMEOUT;
01518 
01519         } else GEN_XSLEEP.MilliSeconds(50);
01520 
01521     } while(1);
01522 
01523   return DIOATCMD_ERROR_UNKNOWN;
01524 }
01525 
01526 
01538 DIOATCMD_ERROR DIOATCMDS::WaitToOneAnswerMore(int timeout)
01539 {
01540   if(!xtimer) return DIOATCMD_ERROR_UNKNOWN;
01541 
01542   int actualnanswer = (int)answers.GetSize();
01543 
01544   xtimer->Reset();
01545 
01546   do{ if((int)xtimer->GetMeasureSeconds()>=timeout)
01547         {
01548           return DIOATCMD_ERROR_TIMEOUT;
01549 
01550         } else GEN_XSLEEP.MilliSeconds(50);
01551 
01552     } while(actualnanswer == (int)answers.GetSize());
01553 
01554   return DIOATCMD_ERROR_NONE;
01555 }
