01579 
01594 DIOATCMD_ERROR DIOATCMDS::MakeCommandWithSimpleAnswer(XDWORD type, XSTRING& answer, XCHAR* param, int timeout)
01595 {
01596   DIOATCMD_ERROR error = SendCommand(type);
01597   if(error!=DIOATCMD_ERROR_NONE) return error;
01598 
01599   int index=0;
01600 
01601   error = WaitToProcessAnswer(timeout,&index);
01602   if(error!=DIOATCMD_ERROR_NONE) return error;
01603 
01604   XSTRING* _answer = GetAnswer(index-1);
01605   if(_answer)
01606     {
01607       answer = (*_answer);
01608       DeleteAnswer(index-1);
01609     }
01610 
01611   return DIOATCMD_ERROR_NONE;
01612 }
