01614 
01626 void DIOATCMDS::ThreadConnection(void* param)
01627 {
01628   DIOATCMDS* dioatcmds = (DIOATCMDS*)param;
01629   if(!dioatcmds) return;
01630 
01631   DIOSTREAM* diostream = dioatcmds->GetDIOStream();
01632   if(!diostream) return;
01633 
01634   if(diostream->GetConnectStatus()!=DIOSTREAMSTATUS_CONNECTED) return;
01635 
01636   XBUFFER* bufferread = diostream->GetInXBuffer();
01637   if(bufferread)
01638     {
01639       if(bufferread->GetSize())
01640         {
01641           if(dioatcmds->mutexreadwriteprocess) dioatcmds->mutexreadwriteprocess->Lock();
01642 
01643           XSTRING answer;
01644 
01645           if(dioatcmds->ReadAnswer(answer))
01646             {
01647               if(!answer.IsEmpty())
01648                 {
01649                   if(dioatcmds->echomode)
01650                     {
01651                       int index = dioatcmds->SearchSendCommand(&answer);
01652                       if(index!=DIOATCMDS_COMMANDNOTFOUND)
01653                         {
01654                           dioatcmds->DeleteSendCommand(index);
01655                           if(dioatcmds->mutexreadwriteprocess) dioatcmds->mutexreadwriteprocess->UnLock();
01656                           return;
01657                         }
01658                     }
01659 
01660                   if(!dioatcmds->CheckUnsolicitedAnswer(answer))
01661                     {
01662                       XSTRING* answerkeep = new XSTRING();
01663                       if(answerkeep)
01664                         {
01665                           (*answerkeep) = answer;
01666                           dioatcmds->answers.Add(answerkeep);
01667                         }
01668                     }
01669                 }
01670             }
01671 
01672           if(dioatcmds->mutexreadwriteprocess) dioatcmds->mutexreadwriteprocess->UnLock();
01673         }
01674     }
01675 }
01676 
01677 
01689 bool DIOATCMDS::CheckUnsolicitedAnswer(XSTRING& answer)
01690 {
01691   if(answer.IsEmpty())  return false;
01692   if(ATcmds.IsEmpty())   return false;
01693 
01694   for(int c=0;c<(int)ATcmds.GetSize();c++)
01695     {
01696       DIOATCMD* atcmd = (DIOATCMD*)ATcmds.Get(c);
01697       if(atcmd)
01698         {
01699           if(atcmd->IsUnSolicited())
01700             {
01701               int index = answer.Find(atcmd->GetCommand(),false);
01702               if(index!=XSTRING_NOTFOUND)
01703                 {
01704                   XSTRING                      param;
01705                   XBUFFER                      additionalparam;
01706                   DIOATCMD_TYPEADDITIONALSIZE  typesize = DIOATCMD_TYPEADDITIONALSIZE_NONE;
01707                   int                          sizedata = 0;
01708 
01709                   answer.Copy(index+atcmd->GetCommandString()->GetSize()+1,param);
01710 
01711                   if(UnsolicitedCommandAddtionalSize(atcmd->GetType(),param,typesize,sizedata))
01712                     {
01713                       switch(typesize)
01714                         {
01715                           case DIOATCMD_TYPEADDITIONALSIZE_NONE   : break;
01716 
01717                           case DIOATCMD_TYPEADDITIONALSIZE_BYTES  : additionalparam.Resize(sizedata);
01718                                                                     diostream->Read((XBYTE*)additionalparam.Get(),sizedata);
01719                                                                     break;
01720 
01721                           case DIOATCMD_TYPEADDITIONALSIZE_LINES  : { XSTRING line;
01722                                                                       int     d = 0;
01723 
01724                                                                       while(d<sizedata)
01725                                                                         {
01726                                                                           if(ReadAnswer(line))
01727                                                                             {
01728                                                                               if(!line.IsEmpty())
01729                                                                                 {
01730                                                                                   XBUFFER charstr;
01731                                                                                   
01732                                                                                   line.ConvertToASCII(charstr);
01733                                                                                   additionalparam.Add((XBYTE*)charstr.Get(), (XDWORD)line.GetSize());
01734                                                                                   
01735                                                                                   d++;
01736                                                                                 }
01737                                                                             }
01738 
01739                                                                         }
01740                                                                     }
01741                                                                     break;
01742                         }
01743 
01744                       return UnsolicitedCommand(atcmd->GetType(),param,additionalparam);
01745 
01746                     } else return false;
01747                 }
01748             }
01749         }
01750     }
01751 
01752   return false;
01753 }
