00441 XDWORD DIOALERT::CalculateID(bool withdatetime)
00442 {
00443   if(!xdatetime)  return false;
00444 
00445   HASHCRC32* hashcrc32 = new HASHCRC32();
00446   if(!hashcrc32) return 0;
00447 
00448   XSTRING IDstring;
00449   XSTRING levelstring;
00450   XDWORD  ID = 0;
00451   bool    status;
00452 
00453   if(withdatetime) xdatetime->GetDateTimeToString(XDATETIME_FORMAT_STANDARD, IDstring);
00454 
00455   levelstring.Format(__L("%s %d"), applicationID.Get(), level);
00456   IDstring += levelstring;
00457 
00458   XRAND* GEN_XFACTORY_CREATE(xrand, CreateRand())
00459   if(xrand)
00460     {
00461       levelstring.Format(__L(" %d "), xrand->Between(1000, 10000000) * xrand->Between(1000, 10000000));
00462       GEN_XFACTORY.DeleteRand(xrand);
00463     }
00464 
00465   IDstring += __L(" ");   IDstring += origin;
00466   IDstring += __L(" ");   IDstring += title;
00467   IDstring += __L(" ");   IDstring += message;
00468   IDstring += levelstring;
00469 
00470   XBUFFER charstr;
00471   
00472   IDstring.ConvertToASCII(charstr);         
00473   status = hashcrc32->Do(charstr.Get(), IDstring.GetSize());
00474 
00475   if(status) ID = hashcrc32->GetResultCRC32(); else ID = 0;
00476 
00477   delete hashcrc32;
00478 
00479   return ID;
00480 }
