00937 bool DIOALERTS::Sender_SMTPConfig(XCHAR* URL, int port, XCHAR* login, XCHAR* password, XCHAR* senderemail, int nrecipients,...)
00938 {
00939   if(!isinitialized) return false;
00940 
00941   if(!SMTP) return false;
00942 
00943   SMTPsenderisactive = false;
00944 
00945   SMTP->Server_GetURL()->Set(URL);
00946   SMTP->Server_SetPort(port);
00947   SMTP->Server_GetLogin()->Set(login);
00948   SMTP->Server_GetPassword()->Set(password);
00949   SMTP->GetSenderEmail()->Set(senderemail);
00950   SMTP->SetContentType(DIOSSMPTCONTENTTYPE_UTF8);
00951 
00952   va_list  arg;
00953 
00954   va_start(arg, nrecipients);
00955 
00956   int nr = nrecipients;
00957 
00958   while(nr)
00959     {
00960       XCHAR* recipient = (XCHAR*)va_arg(arg, XCHAR*);
00961       if(!recipient) break;
00962 
00963       DIOSMTPRECIPIENTTYPE  type        = DIOSMTPRECIPIENTTYPE_UNKNOWN;
00964       int                   test        = 0;
00965       XSTRING               recipientstring;
00966       XSTRING               name;
00967       XSTRING               email;
00968 
00969       recipientstring = recipient;
00970 
00971       name.AdjustSize(_MAXSTR);
00972       email.AdjustSize(_MAXSTR);
00973 
00974       recipientstring.UnFormat(__L("%d,%s,%s,%d"), &type, name.Get(), email.Get(), &test);
00975 
00976       name.AdjustSize();
00977       email.AdjustSize();
00978 
00979       if((type == DIOSMTPRECIPIENTTYPE_TO) || (type == DIOSMTPRECIPIENTTYPE_CC) || (type == DIOSMTPRECIPIENTTYPE_BCC))
00980         {
00981           SMTP->AddRecipient(type, name , email, test?true:false);
00982         }
00983 
00984       nr--;
00985     }
00986 
00987   va_end(arg);
00988 
00989   SMTPsenderisactive = true;
00990 
00991   return SMTPsenderisactive;
00992 }
