01198 bool DIOALERTS::IsValidConditionToSend(XDWORD conditionID)
01199 {
01200   bool mustsend   = true;
01201 
01202   DIOALERT_CONDITION* condition = GetCondition(conditionID);
01203   if(condition)
01204     {
01205       //-------------------------------------------------------------
01206       // Firts send
01207 
01208       if(!condition->GetNSended())
01209         {
01210           condition->GetLastTimerSend()->Reset();
01211         }
01212        else 
01213         {
01214           if(condition->GetTimeLimitForRepeat())
01215             {
01216               if((XDWORD)condition->GetTimeLimitForRepeat() > condition->GetLastTimerSend()->GetMeasureSeconds())
01217                 {
01218                   mustsend = false;
01219                 }
01220             }
01221         
01222           if(condition->GetEveryNumberOfTimes())
01223             {
01224               if(condition->GetNTimesSended() < condition->GetEveryNumberOfTimes())   
01225                 {              
01226                   mustsend = false;  
01227                 } 
01228             }
01229         }    
01230     }
01231 
01232   return mustsend;
01233 }
