01248 int DIOALERTS::Send(DIOALERTSENDER sender, XDWORD conditionID, DIOALERT* alert)
01249 {
01250   if(!isinitialized) return false;
01251 
01252   int   statussend = 0;
01253 
01254   xmutexsending->Lock();  
01255 
01256   if(IsValidConditionToSend(conditionID))
01257     {      
01258       issending = true;
01259 
01260       if((sender & DIOALERTSSENDER_SMPT) && (SMTPsenderisactive)) statussend |= Sender_SMTPSend(alert)? DIOALERTSSENDER_SMPT: 0;
01261       if((sender & DIOALERTSSENDER_SMS)  && (SMSsenderisactive))  statussend |= Sender_SMSSend(alert) ? DIOALERTSSENDER_SMS : 0;
01262       if((sender & DIOALERTSSENDER_WEB)  && (WEBsenderisactive))  statussend |= Sender_WEBSend(alert) ? DIOALERTSSENDER_WEB : 0;
01263       if((sender & DIOALERTSSENDER_UDP)  && (UDPsenderisactive))  statussend |= Sender_UDPSend(alert) ? DIOALERTSSENDER_UDP : 0;
01264       
01265       issending = false;      
01266     }
01267 
01268   DIOALERT_CONDITION* condition = GetCondition(conditionID);
01269   if(condition)
01270     {
01271        if(condition->GetNTimesSended() >= condition->GetEveryNumberOfTimes())   
01272          {              
01273            condition->SetNTimesSended(0);
01274          }
01275 
01276       if(statussend) condition->GetLastTimerSend()->Reset();
01277       condition->SetNSended(condition->GetNSended()+1);
01278       condition->SetNTimesSended(condition->GetNTimesSended()+1);
01279     }
01280 
01281   xmutexsending->UnLock();
01282 
01283   return statussend;
01284 }
