01395   if(SMTPdiostreamcfg)
01396     {
01397       delete SMTPdiostreamcfg;
01398 
01399       SMTPdiostreamcfg = NULL;
01400     }
01401 
01402   SMTPsenderisactive = false;
01403 
01404   //------------------------------------------
01405 
01406   SMSrecipients.DeleteContents();
01407   SMSrecipients.DeleteAll();
01408 
01409   SMSsenderisactive = false;
01410 
01411   //------------------------------------------
01412 
01413   WEBrecipients.DeleteContents();
01414   WEBrecipients.DeleteAll();
01415 
01416   if(WEBdiowebclient)
01417     {
01418       delete WEBdiowebclient;
01419       WEBdiowebclient = NULL;
01420     }
01421 
01422   WEBsenderisactive = false;
01423 
01424   //------------------------------------------
01425 
01426   UDPrecipients.DeleteContents();
01427   UDPrecipients.DeleteAll();
01428 
01429   if(UDPdiostream)
01430     {
01431       UDPdiostream->Close();
01432       delete UDPdiostream;
01433       UDPdiostream = NULL;
01434     }
01435 
01436   if(UDPdiostreamcfg)
01437     {
01438       delete UDPdiostreamcfg;
01439       UDPdiostreamcfg = NULL;
01440     }
01441 
01442   UDPsenderisactive = false;
01443 
01444   //------------------------------------------
01445 
01446   DeleteAllConditions();
01447 
01448   return true;
01449 }
