01496 bool DIOALERTS::Sender_SMTPSend(DIOALERT* alert)
01497 {
01498   if(!SMTP)   return false;
01499   if(!alert)  return false;
01500 
01501   SMTP->GetMessage()->DeleteAllLines();
01502 
01503   XSTRING appIDstring;
01504   XSTRING appverstring;
01505 
01506   SMTP->GetSenderName()->Set(alert->GetApplicationID()->Get());
01507 
01508   if(applicationversion || applicationsubversion  || applicationsubversionerr) appverstring.Format(__L(" %d.%d.%d"), applicationversion, applicationsubversion, applicationsubversionerr);
01509 
01510   appIDstring = alert->GetApplicationID()->Get();
01511   if(!appverstring.IsEmpty()) appIDstring += appverstring.Get();
01512 
01513   appIDstring += __L(": ");
01514   appIDstring += alert->GetTitle()->Get();
01515 
01516   SMTP->GetSubject()->Set(appIDstring);
01517 
01518   XSTRING body;
01519 
01520   body += alert->GetOrigin()->Get();
01521   body += __L("\r\n\r\n");
01522   body += alert->Get_Message()->Get();
01523 
01524   SMTP->GetMessage()->AddLine(body);
01525 
01526   SMTP->SetXPriority(DIOSMTPXPRIORITY_HIGH);
01527 
01528   return SMTP->Send();
01529 }
