01543 bool DIOALERTS::Sender_SMSSend(DIOALERT* alert)
01544 {
01545   if(!alert) return false;
01546 
01547   bool status = false;
01548 
01549   DIOATCMDGSM* SMSdioatcmdgsm = new DIOATCMDGSM( SMSdiostream);
01550   if(SMSdioatcmdgsm)
01551     {
01552       if(SMSdioatcmdgsm->Ini(30, false, false))
01553         {
01554           XSTRING smstext;
01555 
01556           if(alert->GetApplicationID()->GetSize())
01557             {
01558               XSTRING appverstring;
01559 
01560               if(applicationversion || applicationsubversion  || applicationsubversionerr) appverstring.Format(__L(" %d.%d.%d"), applicationversion, applicationsubversion, applicationsubversionerr);
01561 
01562               smstext += __L("<");
01563               smstext += alert->GetApplicationID()->Get();
01564 
01565               if(!appverstring.IsEmpty()) smstext += appverstring.Get();
01566 
01567               smstext += __L("> ");
01568             }
01569 
01570 
01571           if(alert->GetTitle()->GetSize())
01572             {
01573               smstext += alert->GetTitle()->Get();
01574               smstext += __L(" ");
01575             }
01576 
01577 
01578           if(alert->GetOrigin()->GetSize())
01579             {
01580               smstext += __L("(");
01581               smstext += alert->GetOrigin()->Get();
01582               smstext += __L(")");
01583             }
01584 
01585           if(alert->Get_Message()->GetSize())
01586             {
01587               smstext += __L(":");
01588               smstext += alert->Get_Message()->Get();
01589             }
01590 
01591           if(smstext.GetSize()>=160) smstext.AdjustSize(160);
01592 
01593           status = true;
01594 
01595           for(int c=0; c<(int)SMSrecipients.GetSize(); c++)
01596             {
01597               if(SMSdioatcmdgsm->SendSMS(SMSrecipients.Get(c)->Get(), smstext.Get()) != DIOATCMD_ERROR_NONE)  status = false;
01598             }
01599 
01600           SMSdioatcmdgsm->End();
01601         }
01602 
01603       delete SMSdioatcmdgsm;
01604     }
01605 
01606   return status;
01607 }
