01621 bool DIOALERTS::Sender_WEBSend(DIOALERT* alert)
01622 {
01623   if(WEBrecipients.IsEmpty()) return false;
01624 
01625   DIOURL    url;
01626   XSTRING   applicationIDstring;
01627   XSTRING   applicationverstring; 
01628   XBUFFER   webpage;
01629   bool      status  = true;
01630 
01631   if(applicationversion || applicationsubversion  || applicationsubversionerr) applicationverstring.Format(__L(" %d.%d.%d"), applicationversion, applicationsubversion, applicationsubversionerr);
01632 
01633   applicationIDstring = alert->GetApplicationID()->Get();
01634   if(!applicationverstring.IsEmpty()) applicationIDstring += applicationverstring.Get();
01635 
01636   for(int c=0; c<(int)WEBrecipients.GetSize(); c++)
01637     {
01638       DIOURL    part;
01639       
01640       url = WEBrecipients.Get(c)->Get();
01641 
01642       if(WEBisuseget)
01643         {
01644           url.Slash_Delete();
01645           url.AddFormat(__L("?%s") , WEBcommand.Get());
01646 
01647           if(!applicationIDstring.IsEmpty()) 
01648             {
01649               part = applicationIDstring.Get();            
01650               part.EncodeUnsafeChars();                  
01651               url.AddFormat(__L("&%s=%s"), DIOALERTS_QSPARAM_APPLICATIONID, part.Get());       
01652             }      
01653 
01654           if(alert->GetLevel())
01655             {
01656               part.Format(__L("%d"), alert->GetLevel());   
01657               part.EncodeUnsafeChars();                  
01658               url.AddFormat(__L("&%s=%s"), DIOALERTS_QSPARAM_LEVEL, part.Get());               
01659             }
01660 
01661           if(!alert->GetTitle()->IsEmpty())
01662             {
01663               part = alert->GetTitle()->Get();             
01664               part.EncodeUnsafeChars();    
01665               url.AddFormat(__L("&%s=%s"), DIOALERTS_QSPARAM_TITLE, part.Get());              
01666             }
01667               
01668           if(!alert->GetOrigin()->IsEmpty())             
01669             {
01670               part = alert->GetOrigin()->Get();            
01671               part.EncodeUnsafeChars();                  
01672               url.AddFormat(__L("&%s=%s"), DIOALERTS_QSPARAM_ORIGIN, part.Get());              
01673             }
01674 
01675           if(!alert->Get_Message()->IsEmpty())
01676             {
01677               part = alert->Get_Message()->Get();          
01678               part.EncodeUnsafeChars();                  
01679               url.AddFormat(__L("&%s=%s"), DIOALERTS_QSPARAM_MESSAGE, part.Get());             
01680             }
01681              
01682           status = WEBdiowebclient->Get(url.Get(), webpage);          
01683         }
01684        else
01685         {
01686           XSTRING   contents;
01687           XFILEJSON json;
01688           XBUFFER   postbuffer;
01689 
01690           XFILEJSONOBJECT* root   = new XFILEJSONOBJECT();
01691           if(root) 
01692             {
01693               XFILEJSONOBJECT* main  = new XFILEJSONOBJECT();
01694               if(main) 
01695                 {
01696                   XFILEJSON_ADDVALUE(main, DIOALERTS_QSPARAM_APPLICATIONID , applicationIDstring.Get());                  
01697                   XFILEJSON_ADDVALUE(main, DIOALERTS_QSPARAM_LEVEL         , (int)alert->GetLevel());          
01698                   XFILEJSON_ADDVALUE(main, DIOALERTS_QSPARAM_TITLE         , alert->GetTitle()->Get());
01699                   XFILEJSON_ADDVALUE(main, DIOALERTS_QSPARAM_ORIGIN        , alert->GetOrigin()->Get());
01700                   XFILEJSON_ADDVALUE(main, DIOALERTS_QSPARAM_MESSAGE       , alert->Get_Message()->Get());
01701 
01702                   XFILEJSON_ADDVALUE(root, WEBcommand.Get(), main);                      
01703 
01704                   json.SetRoot(root);
01705                   json.EncodeAllLines(false);
01706 
01707                   for(int c=0; c<json.GetNLines(); c++)
01708                     {
01709                       XSTRING* string = json.GetLine(c);
01710                       if(string)
01711                         {                          
01712                           contents.Add(string->Get());                 
01713                         }
01714                      }
01715 
01716                   json.DeleteAllLines();
01717                   json.DeleteAllObjects();
01718                 }
01719             }                    
01720 
01721           //XTRACE_PRINTCOLOR(XTRACE_COLOR_BLUE, __L("%s"), contents.Get());
01722 
01723           XBUFFER postdata;
01724           
01725           contents.ConvertToASCII(postdata);          
01726           postbuffer.Add(postdata.Get(), contents.GetSize());
01727           
01728           status = WEBdiowebclient->Post(url.Get(), webpage, &postbuffer);      
01729         }        
01730     }
01731 
01732   return status;
01733 }
