01747 bool DIOALERTS::Sender_UDPSend(DIOALERT* alert)
01748 {
01749   if(!alert)            return false;
01750   if(!UDPdiostreamcfg)  return false;
01751   if(!UDPdiostream)     return false;
01752 
01753   XBUFFER     xbuffer;
01754   HASHCRC32   hashcrc32;
01755   XDWORD      CRC32;
01756 
01757   xbuffer.Delete();
01758 
01759   // Date time
01760   xbuffer.Add((XDWORD)alert->GetDateTime()->GetDay());                                                              // Day
01761   xbuffer.Add((XDWORD)alert->GetDateTime()->GetMonth());                                                            // Month
01762   xbuffer.Add((XDWORD)alert->GetDateTime()->GetYear());                                                             // Year
01763   xbuffer.Add((XDWORD)alert->GetDateTime()->GetHours());                                                            // Hours
01764   xbuffer.Add((XDWORD)alert->GetDateTime()->GetMinutes());                                                          // Minutes
01765   xbuffer.Add((XDWORD)alert->GetDateTime()->GetSeconds());                                                          // Seconds
01766 
01767   xbuffer.Add((XDWORD)alert->GetApplicationID()->GetSize());    xbuffer.Add((*alert->GetApplicationID()));          // Aplication String
01768   xbuffer.Add((XDWORD)applicationversion);                                                                          // Aplicatcion version
01769   xbuffer.Add((XDWORD)applicationsubversion);                                                                       // Aplication  subversion
01770   xbuffer.Add((XDWORD)applicationsubversionerr);                                                                    // Application subversion err
01771 
01772   xbuffer.Add((XDWORD)alert->GetID());                                                                              // ID  
01773   xbuffer.Add((XDWORD)alert->GetLevel());                                                                           // Level
01774   xbuffer.Add((XDWORD)alert->GetOrigin()->GetSize());           xbuffer.Add((*alert->GetOrigin()));                 // Origin
01775   xbuffer.Add((XDWORD)alert->GetTitle()->GetSize());            xbuffer.Add((*alert->GetTitle()));                  // Title
01776   xbuffer.Add((XDWORD)alert->Get_Message()->GetSize());         xbuffer.Add((*alert->Get_Message()));               // Message
01777 
01778   hashcrc32.ResetResult();
01779   hashcrc32.Do(xbuffer);
01780 
01781   CRC32 = hashcrc32.GetResultCRC32();
01782 
01783   xbuffer.Add((XDWORD)CRC32);
01784 
01785   bool status;
01786   int  nsent = 0;
01787 
01788   for(int c=0; c<(int)UDPrecipients.GetSize(); c++)
01789     {
01790       DIOIP* ip = UDPrecipients.Get(c);
01791       if(ip)
01792         {
01793           XSTRING IPstring;
01794 
01795           ip->GetXString(IPstring);
01796 
01797           UDPdiostreamcfg->SetMode(DIOSTREAMMODE_CLIENT);
01798           UDPdiostreamcfg->GetRemoteURL()->Set(IPstring.Get());
01799           UDPdiostreamcfg->SetIsUsedDatagrams(true);
01800 
01801           status = UDPdiostream->Open();
01802           if(status)
01803             {
01804               status = UDPdiostream->Write(xbuffer.Get(), xbuffer.GetSize())?true:false;
01805               if(status) nsent++;
01806               UDPdiostream->Close();
01807             }
01808         }
01809     }
01810 
01811   return (UDPrecipients.GetSize() == nsent)?true:false;
01812 }
