00279 int DIOAPPLICATIONUPDATE_VERSIONDATA::Compare(XDWORD version, XDWORD subversion, XDWORD subversionerror)
00280 {
00281   // Equal;
00282   if((GetVersion() == version) && (GetSubVersion() == subversion) && (GetSubVersionError() == subversionerror)) return 0;
00283 
00284   bool ismayorversion = false;
00285 
00286   if(GetVersion() > version)
00287     {
00288       ismayorversion = true;
00289     }
00290    else
00291     {
00292       if(GetVersion() == version)
00293         {
00294           if(GetSubVersion() > subversion)
00295             {
00296               ismayorversion = true;
00297             }
00298            else
00299             {
00300               if(GetSubVersion() == subversion)
00301                 {
00302                   if(GetSubVersionError() > subversionerror)  ismayorversion = true;
00303                 }
00304             }
00305         }
00306     }
00307 
00308   return ismayorversion?1:-1;
00309 }
