01085                    status = xfile->Rename(xpathrootappadd, xpathrootappaddrename);
01086                    GEN_XFACTORY.Delete_File(xfile);
01087                  }
01088             }
01089            else
01090             {
01091               error = xevent.GetError();
01092               break;
01093             }
01094         }
01095 
01096       if(status)
01097         {
01098           status = CopyFile(xpathupdateadd, xpathrootappadd);
01099           if(status)
01100             {
01101                if(filestoupdate.Get(c)->HasSpecialControlUpdate() && !xpathrootappaddrename.IsEmpty())
01102                  {
01103                    // Try to erase. In windows probably fails.
01104 
01105                    XFILE* GEN_XFACTORY_CREATE(xfile, Create_File())
01106                    if(xfile)
01107                      {
01108                        xfile->Erase(xpathrootappaddrename);
01109                        GEN_XFACTORY.Delete_File(xfile);
01110                      }
01111                  }
01112             }
01113         }
01114 
01115       DIOAPPLICATIONUPDATE_XEVENT xevent(this, (restore?DIOAPPLICATIONUPDATE_XEVENT_TYPE_RESTOREUPDATEFILE:DIOAPPLICATIONUPDATE_XEVENT_TYPE_UPDATEFILE));
01116 
01117       xevent.SetFilesToUpdate(&filestoupdate);
01118       xevent.SetIndexOfOperation(c);
01119       xevent.SetError(status?DIOAPPLICATIONUPDATE_NOT_ERROR:DIOAPPLICATIONUPDATE_ERROR_INVALIDOPERATION);
01120 
01121       PostEvent(&xevent);
01122 
01123       if(!status)
01124         {
01125           error  = DIOAPPLICATIONUPDATE_ERROR_COPYORIGINALFILE;
01126           break;
01127         }
01128     }
01129 
01130   return status;
01131 }
