01284 bool DIOAPPLICATIONUPDATE::GetListOfVersionsToRestore(XVECTOR<DIOAPPLICATIONUPDATE_VERSIONDATA*>& listversionstorestore)
01285 {
01286   XDIRELEMENT xdirelement;
01287   XDIR*       xdir;
01288   XSTRING     searchstring;
01289   XSTRING     dir_mask;
01290   bool        status = true;
01291 
01292   GEN_XFACTORY_CREATE(xdir, Create_Dir())
01293   if(!xdir) 
01294     {
01295       return false;
01296     }
01297 
01298   dir_mask  = DIOAPPLICATIONUPDATE_RESTOREDIR_ID;
01299   dir_mask += DIOAPPLICATIONUPDATE_RESTOREDIR_MASK;
01300 
01301   searchstring  = DIOAPPLICATIONUPDATE_RESTOREDIR_ID;
01302   searchstring += __L("*");
01303 
01304   if(xdir->FirstSearch(xpathrootapp, searchstring.Get() , &xdirelement))
01305     {
01306       do{  xdirelement.GetNameFile()->Get();
01307 
01308           if(xdirelement.GetType() == XDIRELEMENTTYPE_DIR)
01309             {
01310               if(!xdirelement.GetNameFile()->Find(DIOAPPLICATIONUPDATE_RESTOREDIR_ID, false))
01311                 {
01312                   int version         = 0;
01313                   int subversion      = 0;
01314                   int subversionerror = 0;
01315 
01316                   xdirelement.GetNameFile()->UnFormat(dir_mask.Get(), &version, &subversion, &subversionerror);
01317 
01318                   if(version || subversion || subversionerror)
01319                     {
01320                       DIOAPPLICATIONUPDATE_VERSIONDATA* versiondata = new DIOAPPLICATIONUPDATE_VERSIONDATA();
01321                       if(versiondata)
01322                         {
01323                           versiondata->SetVersion(version);
01324                           versiondata->SetSubVersion(subversion);
01325                           versiondata->SetSubVersionError(subversionerror);
01326 
01327                           listversionstorestore.Add(versiondata);
01328                         }
01329                     }
01330                 }
01331             }
01332 
01333         } while(xdir->NextSearch(&xdirelement));
01334     }
01335 
01336   GEN_XFACTORY.Delete_Dir(xdir);
01337 
01338   return status;
01339 }
