01354 bool DIOAPPLICATIONUPDATE::GetPreviousVersionToRestore(XVECTOR<DIOAPPLICATIONUPDATE_VERSIONDATA*>& listversionstorestore, DIOAPPLICATIONUPDATE_VERSIONDATA& versiontorestore)
01355 {
01356   XVECTOR<int> indexversiontorestore;
01357 
01358   for(XDWORD c=0; c<listversionstorestore.GetSize(); c++)
01359     {
01360       DIOAPPLICATIONUPDATE_VERSIONDATA* versiondata = listversionstorestore.Get(c);
01361       if(versiondata)
01362         {
01363           int comparateversion = versiondata->Compare(applicationversion, applicationsubversion, applicationsubversionerror);
01364           if(comparateversion < 0)
01365             {
01366               indexversiontorestore.Add(c);
01367             }
01368         }
01369     }
01370 
01371   if(!indexversiontorestore.GetSize()) 
01372     {
01373       return false;
01374     }
01375 
01376   if(indexversiontorestore.GetSize() == 1)
01377     {
01378       DIOAPPLICATIONUPDATE_VERSIONDATA* versiondata = listversionstorestore.Get(0);
01379       if(!versiondata) return false;
01380 
01381       versiontorestore.CopyFrom(versiondata);
01382     }
01383    else
01384     {
01385       int indexprevius = indexversiontorestore.GetLast();
01386 
01387       for(XDWORD c=0; c<indexversiontorestore.GetSize(); c++)
01388         {
01389           DIOAPPLICATIONUPDATE_VERSIONDATA* versiondata = listversionstorestore.Get(indexprevius);
01390           DIOAPPLICATIONUPDATE_VERSIONDATA* versiondatacheck = listversionstorestore.Get(indexversiontorestore.Get(c));
01391           if(versiondatacheck && versiondata)
01392             {
01393               if(versiondatacheck->Compare(versiondata->GetVersion(), versiondata->GetSubVersion(), versiondata->GetSubVersionError()) == 1)
01394                 {
01395                   indexprevius = indexversiontorestore.Get(c);
01396                 }
01397             }
01398         }
01399 
01400       DIOAPPLICATIONUPDATE_VERSIONDATA* versiondata = listversionstorestore.Get(indexprevius);
01401       if(!versiondata) return false;
01402 
01403       versiontorestore.CopyFrom(versiondata);
01404     }
01405 
01406   return true;
01407 }
