01520 bool DIOAPPLICATIONUPDATE::EliminateOldRestorations(int maxrestorations)
01521 {
01522   XVECTOR<DIOAPPLICATIONUPDATE_VERSIONDATA*>  listversionstorestore;
01523   XVECTOR<int>                                indexversiontorestore;
01524   bool                                        status = false;
01525 
01526   GetListOfVersionsToRestore(listversionstorestore);
01527 
01528   for(XDWORD c=0; c<listversionstorestore.GetSize(); c++)
01529     {
01530       DIOAPPLICATIONUPDATE_VERSIONDATA* versiondata = listversionstorestore.Get(c);
01531       if(versiondata)
01532         {
01533           int comparateversion = versiondata->Compare(applicationversion, applicationsubversion, applicationsubversionerror);
01534           if(comparateversion < 0)
01535             {
01536               indexversiontorestore.Add(c);
01537             }
01538         }
01539     }
01540 
01541   if(indexversiontorestore.GetSize())
01542     {
01543       for(XDWORD c=0; c<indexversiontorestore.GetSize()-1; c++)
01544         {
01545           for(XDWORD d=1; d<indexversiontorestore.GetSize(); d++)
01546             {
01547               DIOAPPLICATIONUPDATE_VERSIONDATA* versiondata = listversionstorestore.Get(d);
01548               DIOAPPLICATIONUPDATE_VERSIONDATA* versiondatacheck = listversionstorestore.Get(indexversiontorestore.Get(d-1));
01549               if(versiondatacheck && versiondata)
01550                 {
01551                   if(versiondatacheck->Compare(versiondata->GetVersion(), versiondata->GetSubVersion(), versiondata->GetSubVersionError()) == 1)
01552                     {
01553                       int index = indexversiontorestore.Get(c);
01554 
01555                       indexversiontorestore.Set(c, indexversiontorestore.Get(d));
01556                       indexversiontorestore.Set(d, index);
01557                     }
01558                 }
01559             }
01560         }
01561     }
01562 
01563   status = true;
01564 
01565   int toeliminate =  indexversiontorestore.GetSize()-maxrestorations;
01566   if(toeliminate>0)
01567     {
01568       for(int c=0; c<toeliminate; c++)
01569         {
01570           DIOAPPLICATIONUPDATE_VERSIONDATA* versiondata = listversionstorestore.Get(indexversiontorestore.Get(c));
01571           if(versiondata)
01572             {
01573               XPATH     xpath;
01574               XSTRING   dir_mask;
01575 
01576               xpath = xpathrootapp;
01577               xpath.Slash_Add();
01578 
01579               dir_mask  = DIOAPPLICATIONUPDATE_RESTOREDIR_ID;
01580               dir_mask += DIOAPPLICATIONUPDATE_RESTOREDIR_MASK;
01581 
01582               xpath.AddFormat(dir_mask.Get(), versiondata->GetVersion(), versiondata->GetSubVersion(), versiondata->GetSubVersionError());
01583 
01584               XDIR* GEN_XFACTORY_CREATE(xdir, Create_Dir());
01585               if(xdir)
01586                 {
01587                   status = xdir->Delete(xpath.Get(), true);
01588                   GEN_XFACTORY.Delete_Dir(xdir);
01589 
01590                   if(!status) break;
01591                 }
01592 
01593               //XTRACE_PRINTCOLOR(3, xpath.Get());
01594             }
01595         }
01596     }
01597 
01598   listversionstorestore.DeleteContents();
01599   listversionstorestore.DeleteAll();
01600 
01601   indexversiontorestore.DeleteAll();
01602 
01603   return status;
01604 }
