01618 bool DIOAPPLICATIONUPDATE::IsNewVersion(DIOAPPLICATIONUPDATE_VERSIONDATA& updateversiondata)
01619 {
01620   int versioncomparation = updateversiondata.Compare(applicationversion, applicationsubversion, applicationsubversionerror);
01621 
01622   //----------------------------------------------------------------------------------------
01623 
01624   DIOAPPLICATIONUPDATE_XEVENT xevent(this, DIOAPPLICATIONUPDATE_XEVENT_TYPE_CHECKVERSIONAVAILABLE);
01625 
01626   xevent.SetVersionComparation(versioncomparation);
01627   xevent.SetVersionData(&updateversiondata);
01628   xevent.SetFilesToUpdate(&filestoupdate);
01629   xevent.SetError(DIOAPPLICATIONUPDATE_NOT_ERROR);
01630 
01631   PostEvent(&xevent);
01632 
01633   //----------------------------------------------------------------------------------------
01634 
01635   return (versioncomparation == 1)?true:false;
01636 }
