01696 bool DIOAPPLICATIONUPDATE::FilesToUpdate_CreateList(XPATH& xpathupdate)
01697 {
01698   XDIRELEMENT xdirelement;
01699   XDIR*       xdir;
01700   bool        status = true;
01701 
01702   GEN_XFACTORY_CREATE(xdir, Create_Dir())
01703   if(!xdir) return false;
01704 
01705   if(xdir->FirstSearch(xpathupdate, __L("*") , &xdirelement))
01706     {
01707       XPATH xpathadd;
01708 
01709       do{ xpathadd   = xpathupdate;
01710           xpathadd.Slash_Add();
01711           xpathadd  += xdirelement.GetNameFile()->Get();
01712 
01713           // XTRACE_PRINTCOLOR(XTRACE_COLOR_BLUE, __L("%s"), xpathadd.Get());  
01714 
01715           if(xdirelement.GetType() == XDIRELEMENTTYPE_FILE)
01716             {
01717               HASHCRC32 CRC32file;
01718               XQWORD    sizefile = 0;
01719 
01720               CRC32file.Do(xpathadd);
01721 
01722               XFILE* GEN_XFACTORY_CREATE(xfile, Create_File())
01723               if(xfile)
01724                 {
01725                   if(xfile->Open(xpathadd))
01726                     {
01727                       sizefile = xfile->GetSize();
01728                       xfile->Close();
01729                     }
01730 
01731                   GEN_XFACTORY.Delete_File(xfile);
01732                 }
01733 
01734               DIOAPPLICATIONUPDATE_FILE* updatefile = new DIOAPPLICATIONUPDATE_FILE(xpathadd, CRC32file.GetResultCRC32(), sizefile);
01735               if(updatefile)
01736                 {
01737                   XSTRING filename;
01738                   XSTRING fileext;
01739 
01740                   updatefile->GetXPathFile()->GetNamefile(filename);
01741                   updatefile->GetXPathFile()->GetExt(fileext);
01742 
01743                   if(!filename.Compare(applicationname, true))
01744                     {
01745                       updatefile->SetHasSpecialControlUpdate(true);
01746                     }
01747 
01748                   filestoupdate.Add(updatefile);
01749                 }
01750             }
01751 
01752           if(xdirelement.GetType() == XDIRELEMENTTYPE_DIR)
01753             {
01754               if(!FilesToUpdate_CreateList(xpathadd))
01755                 {
01756                   status = false;
01757                   break;
01758                 }
01759             }
01760 
01761         } while(xdir->NextSearch(&xdirelement));
01762     }
01763 
01764   GEN_XFACTORY.Delete_Dir(xdir);
01765 
01766   return status;
01767 }
