01769 
01781 bool DIOAPPLICATIONUPDATE::FilesToUpdate_EliminateFixedDir(XPATH& xpath)
01782 {
01783   XDWORD c = 0;
01784 
01785   if(!filestoupdate.GetSize()) return false;
01786 
01787   while(c < filestoupdate.GetSize())
01788     {
01789       DIOAPPLICATIONUPDATE_FILE* updatefile = filestoupdate.Get(c);
01790       if(updatefile)
01791         {
01792            updatefile->GetXPathFile()->DeleteCharacters(0, xpath.GetSize()+1);
01793            c++;
01794         }
01795     }
01796 
01797   return true;
01798 }
01799 
01800 
01812 bool DIOAPPLICATIONUPDATE::FilesToUpdate_CheckListChangedFiles(XPATH& xpathupdate)
01813 {
01814   XDWORD c = 0;
01815 
01816   while(c < filestoupdate.GetSize())
01817     {
01818       DIOAPPLICATIONUPDATE_FILE* updatefile = filestoupdate.Get(c);
01819       if(updatefile)
01820         {
01821           HASHCRC32 CRC32file;
01822           XPATH     xpath;
01823 
01824           xpath  = xpathrootapp;
01825           xpath.Slash_Add();
01826           xpath += updatefile->GetXPathFile()->Get();
01827 
01828           CRC32file.Do(xpath);
01829 
01830           if(CRC32file.GetResultCRC32() == updatefile->GetCRC32())
01831             {
01832               filestoupdate.Delete(updatefile);
01833               delete updatefile;
01834             }
01835            else
01836             {
01837               c++;
01838             }
01839         }
01840     }
01841 
01842   return true;
01843 }
