01865 
01878 bool DIOAPPLICATIONUPDATE::CopyFile(XPATH& origin, XPATH& target)
01879 {
01880   XFILE* xfile[2] = { NULL, NULL };
01881   bool   status   = false;
01882 
01883   GEN_XFACTORY_CREATE(xfile[0], Create_File())
01884   GEN_XFACTORY_CREATE(xfile[1], Create_File())
01885 
01886   if(xfile[0] && xfile[1])
01887     {
01888       if(xfile[0]->Open(origin))
01889         {
01890           if(xfile[1]->Create(target))
01891             {
01892               #define COPYFILEBLOCKSIZE  500*1024
01893 
01894               XBUFFER xbuffer;
01895               XDWORD  br = COPYFILEBLOCKSIZE;
01896 
01897               xbuffer.Resize(COPYFILEBLOCKSIZE);
01898 
01899               do{ xfile[0]->Read(xbuffer.Get(), &br);
01900                   if(!br) break;
01901 
01902                   if(!xfile[1]->Write(xbuffer.Get(), br)) break;
01903 
01904                 } while(br == COPYFILEBLOCKSIZE);
01905 
01906 
01907               xfile[1]->Close();
01908 
01909               status = true;
01910             }
01911 
01912           xfile[0]->Close();
01913         }
01914     }
01915 
01916   GEN_XFACTORY.Delete_File(xfile[0]);
01917   GEN_XFACTORY.Delete_File(xfile[1]);
01918 
01919   if(status)
01920     {
01921       status = false;
01922 
01923       HASHCRC32* CRC32file[2];
01924 
01925       CRC32file[0] = new HASHCRC32();
01926       CRC32file[1] = new HASHCRC32();
01927 
01928       if(CRC32file[0] && CRC32file[1])
01929         {
01930           CRC32file[0]->Do(origin);
01931           CRC32file[1]->Do(target);
01932 
01933           if(CRC32file[0]->GetResultCRC32() == CRC32file[1]->GetResultCRC32()) status = true;
01934         }
01935 
01936       delete CRC32file[0];
01937       delete CRC32file[1];
01938     }
01939 
01940   return status;
01941 }
