00951 bool DIOCHECKTCPIPCONNECTIONS::Connections_WaitToAllChecked(int timeout)
00952 {
00953   if(!connections.GetSize()) return false;
00954 
00955   XTIMER* GEN_XFACTORY_CREATE(xtimer, CreateTimer())
00956   if(!xtimer) return false;
00957 
00958   bool status = true;
00959 
00960   xtimer->Reset();
00961 
00962   SetIsCheckTimeConnections(false);
00963 
00964   while(1)
00965     {
00966       if(timeout != XTIMER_INFINITE)
00967         {
00968           if(xtimer->GetMeasureSeconds() >= (XDWORD)timeout)
00969             {
00970               status = false;
00971               break;
00972             }
00973         }
00974 
00975       if(Connections_IsAllChecked()) break;
00976 
00977       GEN_XSLEEP.MilliSeconds(100);
00978     }
00979 
00980   GEN_XFACTORY.DeleteTimer(xtimer);
00981 
00982   SetIsCheckTimeConnections(true);
00983 
00984   return status;
00985 }
