00391 bool DIODNSPROTOCOLCLIENT::EncodeDNSFormat(XSTRING& URL, XBUFFER& EncodedURL) 
00392 {
00393   int subsize = 0;
00394 
00395   if(URL.IsEmpty())
00396     {
00397       return false;
00398     }
00399   
00400   for(int c=0; c<URL.GetSize(); c++)
00401     {
00402       for(int d=c; d<URL.GetSize(); d++)
00403         {
00404           if(URL.Get()[d] != __C('.'))
00405             {
00406               subsize++;  
00407             }
00408            else 
00409             {
00410               break;
00411             } 
00412         }
00413 
00414       EncodedURL.Add((XBYTE)subsize);
00415 
00416       for(int d=0; d<subsize; d++)
00417         {
00418           EncodedURL.Add((XBYTE)URL.Get()[c]);
00419           c++;
00420         }  
00421 
00422       subsize = 0;
00423     }
00424 
00425   EncodedURL.Add((XBYTE)0x00);
00426    
00427   return true;
00428 }
