00517 XBYTE* DIODNSPROTOCOLCLIENT::GetBufferName(XBYTE* reader, XBYTE* buffer, int* count)
00518 {
00519   XBYTE*  name;
00520   XDWORD  p       = 0;
00521   XDWORD  jumped  = 0;
00522   XDWORD  offset;
00523   int     i;
00524   int     j;
00525 
00526   *count = 1;
00527   name = (XBYTE*)malloc(_MAXSTR);
00528 
00529   name[0] = '\0';
00530 
00531   //read the names in 3www6google3com format
00532   while(*reader != 0)
00533     {
00534       if(*reader >= 192)
00535         {
00536           offset = (*reader)*256 + *(reader+1) - 49152; //49152 = 11000000 00000000 ;)
00537           reader = buffer + offset - 1;
00538           jumped = 1; //we have jumped to another location so counting wont go up!
00539         }
00540        else
00541         {
00542           name[p++] = *reader;
00543         }
00544 
00545      reader = reader + 1;
00546 
00547      if(jumped == 0)
00548        {
00549          *count = *count + 1; //if we havent jumped to another location then we can count up
00550        }
00551     }
00552 
00553   name[p] = '\0'; //string complete
00554   if(jumped==1)
00555     {
00556       *count = *count + 1; //number of steps we actually moved forward in the packet
00557     }
00558 
00559   //now convert 3www6google3com0 to www.google.com
00560   for(i=0; i<(int)strlen((const char*)name); i++)
00561     {
00562       p = name[i];
00563 
00564       for(j=0; j<(int)p; j++)
00565         {
00566           name[i]=name[i+1];
00567           i = i + 1;
00568         }
00569 
00570       name[i]='.';
00571     }
00572 
00573   name[i-1]='\0'; //remove the last dot
00574 
00575   return name;
