00718 void DIOESP32STREAMI2C::ThreadConnection(void* param)
00719 {
00720   DIOESP32STREAMI2C* diostream = (DIOESP32STREAMI2C*)param;
00721   if(!diostream) return;
00722 
00723   if(diostream->GetEvent()==DIOSTREAMI2C_FSMEVENT_NONE)
00724     {
00725       switch(diostream->GetCurrentState())
00726         {
00727           case DIOSTREAMI2C_FSMSTATE_NONE                   : break;
00728 
00729           case DIOSTREAMI2C_FSMSTATE_CONNECTED              : break;
00730 
00731           case DIOSTREAMI2C_FSMSTATE_WAITINGTOREAD          : if(!diostream->IsBlockRead())
00732                                                                 {
00733                                                                   if(!diostream->inbuffer->IsBlocked())
00734                                                                     {
00735                                                                       if(diostream->readcache[diostream->indexport].GetSize())
00736                                                                         {
00737                                                                           diostream->inbuffer->Add(diostream->readcache[diostream->indexport]);
00738                                                                           diostream->readcache[diostream->indexport].Delete();
00739                                                                         }
00740                                                                     }
00741                                                                 }
00742 
00743                                                               if(!diostream->IsBlockWrite())
00744                                                                 {
00745                                                                   if(!diostream->outbuffer->IsBlocked())
00746                                                                     {
00747                                                                       int esize = diostream->outbuffer->GetSize();
00748                                                                       if(esize>DIOSTREAM_MAXBUFFER) esize = DIOSTREAM_MAXBUFFER;
00749 
00750                                                                       if(esize)
00751                                                                         {
00752                                                                           diostream->outbuffer->Extract(diostream->writebuffer, 0, esize);
00753                                                                           XDWORD _esize = diostream->WriteDirect(diostream->writebuffer, esize);
00754                                                                           if(_esize != esize)
00755                                                                             {
00756                                                                               diostream->outbuffer->Add(diostream->writebuffer, esize);
00757                                                                             }                                                                        
00758                                                                         }
00759                                                                     }
00760                                                                 }
00761                                                               break;
00762 
00763           case DIOSTREAMI2C_FSMSTATE_DISCONNECTING          : break;
00764 
00765         }
00766     }
00767    else
00768     {
00769       if(diostream->GetEvent()<DIOSTREAMI2C_LASTEVENT)
00770         {
00771           diostream->CheckTransition();
00772 
00773           switch(diostream->GetCurrentState())
00774             {
00775               case DIOSTREAMI2C_FSMSTATE_NONE               : break;
00776 
00777               case DIOSTREAMI2C_FSMSTATE_CONNECTED          : { DIOSTREAMXEVENT xevent(diostream,DIOSTREAMXEVENT_TYPE_CONNECTED);
00778                                                                 xevent.SetDIOStream(diostream);
00779                                                                 diostream->PostEvent(&xevent);
00780 
00781                                                                 diostream->SetEvent(DIOSTREAMI2C_FSMEVENT_WAITINGTOREAD);
00782                                                               }
00783                                                               break;
00784 
00785               case DIOSTREAMI2C_FSMSTATE_WAITINGTOREAD      : break;
00786 
00787               case DIOSTREAMI2C_FSMSTATE_DISCONNECTING      : { DIOSTREAMXEVENT xevent(diostream,DIOSTREAMXEVENT_TYPE_DISCONNECTED);
00788                                                                 xevent.SetDIOStream(diostream);
00789                                                                 diostream->PostEvent(&xevent);
00790 
00791                                                                 diostream->status = DIOSTREAMSTATUS_DISCONNECTED;
00792                                                               }
00793                                                               break;
00794             }
